import React, { useContext, useState } from 'react';
import { useHistory, Link } from 'react-router-dom';
import './Header.css'
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import { Badge } from '@material-ui/core';
import { clientContext } from '../../Context/ClientContext';
import { Card, Button, Alert, Dropdown } from "react-bootstrap"
import { useAuth } from "../../Context/AuthContext"

const Header = () => {
    const history = useHistory()
    const { productsCountInCart, getProducts } = useContext(clientContext)
    const [error, setError] = useState("")
    const { currentUser, logout } = useAuth()

    async function handleLogout() {
        setError("")

        try {
            await logout()
            history.push("/login")
        } catch {
            setError("Failed to log out")
        }
    }

    return (
        <div className="container">
            <div className="header">
                <div className="header-left">
                    <div className="header-info">
                        <div className="logo">
                            <img className="img-logo" src="https://image.flaticon.com/icons/png/512/123/123268.png" alt="logo" />
                        </div>
                        <p className="header-info__text">Доставка всего за полчаса</p>
                        <p className="header-info__text">0550346897</p>
                    </div>
                    <div className="header-filter">
                        <ul className="filter-list">
                            <p>шаурма</p>
                            <p>напитки</p>
                            <p>акции</p>
                            <p>другое</p>
                        </ul>
                    </div>
                </div>
                <div className="navbar-items">
                    {

                    }
                    <button
                        className="add-btn"
                        onClick={() => history.push('/addproduct')}>
                        +
                    </button>
                    <div className="auth">
                        <Card>
                            <Card.Body>
                                <h2 className="text-center mb-4">Profile</h2>
                                {error && <Alert variant="danger">{error}</Alert>}
                                <strong>Email:</strong> {currentUser.email}
                                <Link to="/update-profile" className="btn btn-primary w-100 mt-3">
                                    Update Profile
                                </Link>
                            </Card.Body>
                        </Card>
                        <Dropdown>
                            <Dropdown.Toggle variant="success" id="dropdown-basic">
                                Dropdown Button
                            </Dropdown.Toggle>

                            <Dropdown.Menu>
                                <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
                                <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
                                <Dropdown.Item onClick={handleLogout} href="#/action-3">Log Out</Dropdown.Item>
                            </Dropdown.Menu>
                        </Dropdown>
                    </div>
                    <Link to="/cart">
                        <button className="check-cart__btn">
                            <Badge badgeContent={productsCountInCart} color="secondary">
                                <ShoppingCartIcon />
                            </Badge>
                        </button>
                    </Link>
                </div>
            </div>
        </div >
    );
};

export default Header;
