import React, { useContext, useState } from 'react';
import { clientContext } from '../../Context/ClientContext';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import { Button, Input } from '@material-ui/core';
import { useHistory } from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
    modal: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    paper: {
        backgroundColor: theme.palette.background.paper,
        boxShadow: theme.shadows[5],
        padding: theme.spacing(2, 4, 3),
        height: 350,
        width: 300,
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-evenly'
    },
}));

export default function TransitionsModal() {
    const classes = useStyles();
    const [open, setOpen] = React.useState(false);

    const handleOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const { loginUser } = useContext(clientContext)
    const history = useHistory()
    const [user, setUser] = useState({
        email: "",
        password: ""
    })
    function handleInput(e) {
        let obj = {
            ...user,
            [e.target.name]: e.target.value
        }
        setUser(obj)
    }
    function handleClick() {
        loginUser(user, history)
    }

    return (
        <div>
            <button type="button" onClick={handleOpen}>
                Sign up
            </button>
            <Modal
                aria-labelledby="transition-modal-title"
                aria-describedby="transition-modal-description"
                className={classes.modal}
                open={open}
                onClose={handleClose}
                closeAfterTransition
                BackdropComponent={Backdrop}
                BackdropProps={{
                    timeout: 500,
                }}
            >
                <Fade in={open}>
                    <div className={classes.paper}>
                        <Input onChange={handleInput} name="email" type="text" placeholder="email" />
                        <Input onChange={handleInput} name="password" type="password" placeholder="Password" />
                        <Button variant="contained" color="primary" onClick={handleClick}>Sign in</Button>
                    </div>
                </Fade>
            </Modal>
        </div>
    );
}
